	WHEN mid(START, 5, 3) = 'Ago' THEN
		8
	WHEN mid(START, 5, 3) = 'Sep' THEN
		9
	WHEN mid(START, 5, 3) = 'Oct' THEN
		10
	WHEN mid(START, 5, 3) = 'Nov' THEN
		11
	WHEN mid(START, 5, 3) = 'Dec' THEN
		12
	END,
	'-',
	mid(START, 9, 2),
	' ',
	mid(START, 17, 8)
	) AS Fecha into resultado
FROM
	tblagenda;


END ;;
